mapper.select:
SELECT COUNT(*) FROM member m LEFT OUTER JOIN member_detail d ON m.mem_num=d.mem_num WHERE m.mem_auth IN(0,1,2)
AND m.mem_auth = #{auth_num}
AND m.mem_num = #{keyword}
AND m.mem_id LIKE '%' || #{keyword} || '%'
AND d.mem_phone LIKE '%' || #{keyword} || '%'

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM member m LEFT OUTER JOIN member_detail d ON m.mem_num=d.mem_num WHERE m.mem_auth IN(0,1,2)
AND m.mem_auth = #{auth_num}
AND m.mem_num = #{keyword}
AND m.mem_id LIKE '%' || #{keyword} || '%'
AND d.mem_phone LIKE '%' || #{keyword} || '%'
ORDER BY m.mem_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
